    public static boolean isXperiaPlay() {
        String[] tags = {Build.MODEL, Build.DEVICE, Build.PRODUCT};
        for (String tag : tags) {
            tag.toLowerCase(Locale.ENGLISH);
            if (tag.indexOf("r800") >= 0 || tag.indexOf("so-01d") >= 0 || (tag.indexOf("xperia") >= 0 && tag.indexOf("play") >= 0)) {
                return true;
            }
        }
        return false;
    }

