    public static void saveScreenshot(String filename, int w, int h, int[] pixels) {
        Bitmap bitmap = Bitmap.createBitmap(pixels, w, h, Config.ARGB_8888);
        try {
            FileOutputStream fos = new FileOutputStream(filename);
            bitmap.compress(CompressFormat.JPEG, 85, fos);
            try {
                fos.flush();
            } catch (IOException e) {
                e.printStackTrace();
            }
            try {
                fos.close();
            } catch (IOException e2) {
                e2.printStackTrace();
            }
        } catch (FileNotFoundException e3) {
            System.err.println("Couldn't create file: " + filename);
            e3.printStackTrace();
        }
    }

    private static void copyFile(InputStream in, OutputStream out) throws IOException {
        byte[] buff = new byte[1024];
        int len = in.read(buff);
        while (len != -1) {
            out.write(buff, 0, len);
            len = in.read(buff);
        }
        in.close();
        out.close();
    }

    private static void copyAssetDir(AssetManager am, String path, String outpath) {
        Log.w("MONO", "EXTRACTING: " + path);
        try {
            String[] res = am.list(path);
            int length = res.length;
            for (int i = 0; i < length; i += 1) {
                String result = res[i];
                String fromFile = path + "/" + result;
                String toFile = outpath + "/" + result;
                if (fromFile.endsWith(".dll")) {
                    Log.w("MONO", "\tCOPYING " + fromFile + " to " + toFile);
                    copyFile(am.open(fromFile), new FileOutputStream(toFile));
                } else {
                    Log.w("MONO", "\t" + fromFile + " is not a dll, skipping");
                }
            }
        } catch (Exception e) {
            Log.w("MONO", "DLL copy failed: ", e);
        }
    }

